{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code]\nimport imageio\nimport matplotlib.pyplot as plt\nimport numpy as np\nfrom tensorflow.keras.models import load_model\n\n# Load the saved model\nloaded_model = load_model(\"VGG19.h5\") # Update the saved model\n\n\n# Predict labels for test images\ny_pred = loaded_model.predict(test)\n\n# Get class labels for test images\nclass_labels = list(test.class_indices.keys())\n\n# Visualize test images and predicted labels\nfig, axs = plt.subplots(2, 4, figsize=(12, 6))\naxs = axs.flatten()\nfor i in range(8):\n    # Get test image and label\n    img, label = test[i]\n    img = img[0]  # Since we're using batch_size=32, take the first image in the batch\n    label_idx = np.argmax(label)  # Get the index of the true label in one-hot format\n\n    # Plot image\n    axs[i].imshow(img)\n    axs[i].axis(\"off\")\n    # Get predicted label and confidence score\n    pred_label = class_labels[np.argmax(y_pred[i])]\n    confidence = np.max(y_pred[i])\n    # Get true label\n    true_label = class_labels[label_idx]  # Get the class label from the index\n    # Set title with true and predicted labels and confidence score\n    axs[i].set_title(f\"True: {true_label}\\nPred: {pred_label}\\nConfidence: {confidence:.2f}\")\n\nplt.tight_layout()\nplt.show()","metadata":{"_uuid":"860d5de8-8008-49f3-9629-c8ba486ab3d9","_cell_guid":"ab2f4fb3-3e19-48c1-a556-af256e502c48","collapsed":false,"jupyter":{"outputs_hidden":false},"trusted":true},"execution_count":null,"outputs":[]}]}